export const ADD_BOOK = "ADD_BOOK", EDIT_BOOK = "EDIT_BOOK", DELETE_BOOK = "DELETE_BOOK", TOGGLE_READ = "TOGGLE_READ", SET_FILTER = "SET_FILTER";
export const addBook = (b) => ({ type: ADD_BOOK, payload: b });
export const editBook = (b) => ({ type: EDIT_BOOK, payload: b });
export const deleteBook = (id) => ({ type: DELETE_BOOK, payload: id });
export const toggleRead = (id) => ({ type: TOGGLE_READ, payload: id });
export const setFilter = (f) => ({ type: SET_FILTER, payload: f });
